function restoreDatabase(e,t){statusApp.showPleaseWait();var n=$.ajax({type:"POST",url:site_url+"disaster-recovery",async:false,data:{database:e,timestamp:t,_ajaxFunc:"restoreDatabase"}});statusApp.hidePleaseWait();n=$.parseJSON(n.responseText);popUpMsg(n.msg)}function dumpDatabase(){statusApp.showPleaseWait();var e=$.ajax({type:"POST",url:site_url+"disaster-recovery",async:false,data:{database:"dashboard",_ajaxFunc:"dumpDatabase"}});reloadFormElement("backup_entries","disaster-recovery");statusApp.hidePleaseWait();e=$.parseJSON(e.responseText);popUpMsg(e.msg)}$(document).ready(function(){$("#backupBtn").click(function(){dumpDatabase()});$("#backupTbl").tablesorter()})