function reloadPageElements(e,t){$("#"+e+"_form")[0].reset();$("#"+e+"_form #_id").val("");$("#"+e+"_select").prop("selectedIndex",0);if(t){reloadFormElement("users_select_container","users");reloadFormElement("client-writer-list","users");$("#users_select").change(function(){updateForm($(this).val(),"users",["mongoid","fullname","password","email","type","status"])})}}function clientToUserForm(e){if(e!=""){var t=$.ajax({type:"POST",dataType:"json",url:site_url+"users",async:false,data:{_id:e,collection:"clients",mongoid:true,_ajaxFunc:"getEntry"}});var n=$.parseJSON(t.responseText);$("#users_form")[0].reset();$("#users_form #_id").val(n._id["$id"]);$("#users_form #fullname").val(n.client_name);$("#users_form #email").val(n.email);$("#users_form #type").val("CLIENT");$("#users_form #status").val(1)}}function writerToUserForm(e){if(e!=""){var t=$.ajax({type:"POST",dataType:"json",url:site_url+"users",async:false,data:{_id:e,collection:"writers",mongoid:true,_ajaxFunc:"getEntry"}});var n=$.parseJSON(t.responseText);$("#users_form")[0].reset();$("#users_form #_id").val(n._id["$id"]);$("#users_form #fullname").val(n.writer_name);$("#users_form #email").val(n.email);$("#users_form #type").val(n.writer_type);$("#users_form #status").val(1)}}$(document).ready(function(){$("#users_select").change(function(){updateForm($(this).val(),"users",["mongoid","fullname","password","email","type","status"])});$("#submitBtn").click(function(){saveDoc("users",true)});$("#deleteBtn").click(function(){deleteDoc("users",true)});$("#resetBtn").click(function(){reloadPageElements("users",false)})})