function processLogin(){if($("#login_username").val()==""){popUpMsg("User name must be filled.");return false}else if($("#login_password").val()==""){popUpMsg("Password must be filled.");return false}var e={};e["type"]=$("#login_type").val();e["email"]=$("#login_email").val();e["password"]=$("#login_password").val();var t=site_url+"login";var n=$.ajax({type:"POST",url:t,async:false,data:{values:e,_ajaxFunc:"processLogin"}});success=n.responseText;if(success!=""){$("input").css("border","");if(success=="error"){popUpMsg("User validation as failed.");$("input").css("border","1px solid #ff0000");return false}else if(success=="restricted"){popUpMsg("User is not authorized.");$("input").css("border","1px solid #ff0000");return false}else if(success=="pass"){location.replace(site_url)}}}$(document).ready(function(){$("#login_form").addClass("col-lg-offset-2");$("#login_password").keypress(function(e){if(e.which==13){processLogin()}});$("#login_email").keypress(function(e){if(e.which==13){processLogin()}})})