function saveOrder(){if(validateForm("place_order_form")!=false){statusApp.showPleaseWait();var e=createDocFromForm("place_order_form");var t=$.ajax({type:"POST",url:site_url+"orders",async:false,data:{doc:e,_ajaxFunc:"saveEntry"}});t=$.parseJSON(t.responseText);statusApp.hidePleaseWait();if(t.err==null){popUpMsg("Your order has been placed!");$("#place_order_form")[0].reset();return true}else{popUpMsg(t.err);return false}}return false}function createFileList(e){clientAttachments.push(e)}function removeFromFileList(e){var t=[];for(var n=0;n<clientAttachments.length;n++){if(clientAttachments[n]!=e){t.push(clientAttachments[n])}}clientAttachments=t}function sendUploadNotification(){if(clientAttachments.length>0){statusApp.showPleaseWait();var e=$.ajax({type:"POST",url:site_url+"orders",async:false,data:{filenames:clientAttachments,_ajaxFunc:"sendUploadNotification"}});statusApp.hidePleaseWait();var t="The following files have been uploaded:<br />";t+=clientAttachments.join("<br />");if(uploadErrors.length>0){t+="<br /><br />";t+=uploadErrors.join("<br />")}popUpMsg(t)}else if(uploadErrors.length>0){var t=uploadErrors.join("<br />");popUpMsg(t)}}var clientAttachments=[];var uploadErrors=[];$(document).ready(function(){$("#timestamp").prop("disabled",true);$("#token").prop("disabled",true);$("#client_id").prop("disabled",true);$("#description_container").addClass("col-xs-12");$("#description").prop("rows",15);$("#submitBtn").click(function(){saveOrder()});if($("#project_tags_1").length==0){$("#project_tags_container").hide()}$("#uploadBtn").click(function(){$("#file_upload").uploadifive("upload")});$("#file_upload").uploadifive({formData:{timestamp:$("#timestamp").val(),token:$("#token").val(),client_id:$("#client_id").val()},buttonText:"UPLOAD ATTACHMENTS",removeCompleted:true,fileType:["application/msword","application/pdf"],width:250,uploadScript:site_url+"Utilities/uploadifive/upload-client-files.php",onUploadFile:function(e){createFileList(e.name)},onError:function(e,t){if(t!=null){removeFromFileList(t.name);uploadErrors.push("The file "+t.name+" could not be uploaded: "+e)}else{uploadErrors.push("An error has occured: "+e)}},onQueueComplete:function(e){sendUploadNotification();clientAttachments=[];uploadErrors=[]}})})