function saveOrder(){if(validateForm("place_order_form")!=false){statusApp.showPleaseWait();var e=createDocFromForm("place_order_form");var t=$.ajax({type:"POST",url:site_url+"orders",async:false,data:{doc:e,_ajaxFunc:"saveEntry"}});t=$.parseJSON(t.responseText);statusApp.hidePleaseWait();if(t.err==null){popUpMsg("Your order has been placed!");$("#place_order_form")[0].reset();return true}else{popUpMsg(t.err);return false}}return false}$(document).ready(function(){$("#description_container").addClass("col-xs-12");$("#description").prop("rows",15);$("#submitBtn").click(function(){saveOrder()});if($("#project_tags_1").length==0){$("#project_tags_container").hide()}})