function processLogin(){if($("#login_username").val()==""){popUpMsg("User name must be filled.");return false}else if($("#login_password").val()==""){popUpMsg("Password must be filled.");return false}var e={};e["type"]=$("#login_type").val();e["email"]=$("#login_email").val();e["password"]=$("#login_password").val();var t=site_url+"login";if(e["type"]=="ADMIN"){t=site_url+"admin/login"}var n=$.ajax({type:"POST",url:t,async:false,data:{values:e,_ajaxFunc:"processLogin"}});success=n.responseText;if(success!=""){$("input").css("border","");if(success=="error"){popUpMsg("User validation as failed.");$("input").css("border","1px solid #ff0000");return false}else if(success=="restricted"){popUpMsg("User is not authorized.");$("input").css("border","1px solid #ff0000");return false}else if(success=="pass"){if($("#login_type").val()=="ADMIN"){location.replace(site_url+"admin")}else{location.replace(site_url)}}}}function displayErrors(e){e=e.split(",");for(var t=0;t<e.length;t++){$("#"+e[t]).css("border","1px solid #ff0000")}}