function saveFeedback(){if(validateForm("feedback_form")!=false){statusApp.showPleaseWait();var e=createDocFromForm("feedback_form");e.project_id=$("#projects_select").val();e.anonymous=$("#anonymous_select").val();var t=$.ajax({type:"POST",url:site_url+"feedback",async:false,data:{doc:e,_ajaxFunc:"saveEntry"}});t=$.parseJSON(t.responseText);statusApp.hidePleaseWait();if(t.err==null){popUpMsg("Thank you for your feedback!");$("#feedback_form")[0].reset();return true}else{popUpMsg(t.err);return false}}return false}$(document).ready(function(){$("#description_container").addClass("col-xs-12");$("#description").prop("rows",15);$("#submitBtn").click(function(){saveFeedback()});$("#anonymous_select").change(function(){if($(this).val()>0){$("#projects_select").prop("disabled",false)}else{$("#projects_select").val("");$("#projects_select").prop("disabled",true)}})})