function loadInvoice(e){if(e!=""){statusApp.showPleaseWait();var t=$.ajax({type:"POST",url:site_url+"invoices",async:false,data:{_id:e,dom_id:"writer-invoice",_ajaxFunc:"renderPageElement"}});$("#writer-invoice").html(t.responseText);statusApp.hidePleaseWait();$("#invoiceDetailsTbl").tablesorter()}}function printInvoice(e){if(e!=""){statusApp.showPleaseWait();var t=$.ajax({type:"POST",url:site_url+"invoices",async:false,data:{_id:e,_ajaxFunc:"openInvoicePDF"}});var t=$.parseJSON(t.responseText);statusApp.hidePleaseWait();window.open(t.file_path,"_blank")}}$(document).ready(function(){$(".loadInvoice").click(function(){var e=$(this).attr("id").split("invoice_")[1];loadInvoice(e)});$(".printInvoice").click(function(){var e=$(this).attr("id").split("print_")[1];printInvoice(e)});$("#invoiceHistoryTbl").tablesorter()})