function saveTheDate(){if($("#datepicker").val()==""){popUpMsg("You need to pick a date!");return false}else if($("#hours").val()==""){popUpMsg("You need to select your amount of hours!");return false}var e={};e.url="schedule",e.collection="writers",e.set="schedule",e.values={};e.values.title="\nHours: "+$("#hours").val();e.values.start=$("#datepicker").val();e.values.description=$("#description").val();addSetToDoc(e);$("#writer-schedule").fullCalendar("refetchEvents")}$(document).ready(function(){$("#writer-schedule").fullCalendar({header:{left:"",center:"title",right:""},events:{url:site_url+"schedule",data:{_ajaxFunc:"gatherScheduledEvents"}},eventRender:function(e,t){if(e.description!=""){t.append("Description: "+e.description)}},firstDay:1,defaultDate:$("#nextMonday").val(),defaultView:"basicWeek",editable:false});$("#datepicker").datepicker({firstDay:1,beforeShowDay:function(e){var t=new Date;t.setHours(0,0,0,0);t.setDate(t.getDate()+1-(t.getDay()||7)+7);var n=new Date(t);n.setDate(t.getDate()+6);return[e>=t&&e<=n,""]}});$("#submitBtn").click(function(){saveTheDate()});$("#resetBtn").click(function(){$("#writer-schedule").fullCalendar("refetchEvents")})})